document.addEventListener("DOMContentLoaded", () => {
    // Sales Data (Example)
    const salesData = {
        x: ['January', 'February', 'March', 'April', 'May'],
        y: [20000, 30000, 25000, 40000, 45000],
        type: 'scatter',
        mode: 'lines+markers',
        name: 'Sales',
        marker: { color: 'blue' }
    };

    const expensesData = {
        x: ['January', 'February', 'March', 'April', 'May'],
        y: [10000, 15000, 12000, 18000, 20000],
        type: 'scatter',
        mode: 'lines+markers',
        name: 'Expenses',
        marker: { color: 'red' }
    };

    const profitsData = {
        x: ['January', 'February', 'March', 'April', 'May'],
        y: [10000, 15000, 13000, 22000, 25000],
        type: 'scatter',
        mode: 'lines+markers',
        name: 'Profits',
        marker: { color: 'green' }
    };

    const staffPaymentsData = {
        x: ['January', 'February', 'March', 'April', 'May'],
        y: [5000, 7000, 6000, 8000, 10000],
        type: 'scatter',
        mode: 'lines+markers',
        name: 'Staff Payments',
        marker: { color: 'orange' }
    };

    const grantFundingData = {
        x: ['January', 'February', 'March', 'April', 'May'],
        y: [3000, 4000, 5000, 6000, 7000],
        type: 'scatter',
        mode: 'lines+markers',
        name: 'Grant Funding',
        marker: { color: 'purple' }
    };

    // Create the plots
    Plotly.newPlot('salesChart', [salesData]);
    Plotly.newPlot('expensesChart', [expensesData]);
    Plotly.newPlot('profitsChart', [profitsData]);
    Plotly.newPlot('staffPaymentsChart', [staffPaymentsData]);
    Plotly.newPlot('grantFundingChart', [grantFundingData]);
});
